    def select_file(self):
        dialog = QtWidgets.QFileDialog()
        filename, _ = dialog.getOpenFileName(None, 
                                          "Escolha um arquivo", 
                                          "", 
                                          "Arquivo de imagem (*.jpg)");
        self.lineEdit_path.setText(filename)
        
    def process_file(self):
        filename = self.lineEdit_path.text()
        if filename == '':
            file_not_selected = DialogFactory(title='Erro',
                                              message='Nenhum arquivo selecionado',
                                              type='critical',
                                              ok_button=True)
            file_not_selected.show()
            file_not_selected.wait()
            return
        
        processing_dialog = DialogFactory(title='Processando', message='Processando arquivo')
        processing_dialog.show()
        
        self.p = Process(filename)
        positions = self.p.run()
        
        processing_dialog.close()
        
        if positions:
            success_dialog = DialogFactory(title='Pronto',
                                           message='Processado com sucesso',
                                           ok_button=True)
            success_dialog.show()
            success_dialog.wait()
            
            msg = f'O ponto se encontra {positions[0]} e {positions[1]}'
            
            position_dialog = DialogFactory(title='Posição',
                                            message=msg,
                                            ok_button=True)
            position_dialog.show()
            position_dialog.wait()
            
        else:
            failed_dialog = DialogFactory(title='Erro',
                                          message='Um erro desconhecido aconteceu!',
                                          type='critical',
                                          ok_button=True)
            failed_dialog.show()
            failed_dialog.wait()
            return
        
    def show_processed(self):
        try:
            self.p.show()
        except Exception as e:
            failed_dialog = DialogFactory(title='Erro',
                                          message='Imagem não foi processada ainda!',
                                          type='critical',
                                          ok_button=True)
            failed_dialog.show()
            failed_dialog.wait()
            
    def show_raw(self):
        filename = self.lineEdit_path.text()
        if filename == '':
            file_not_selected = DialogFactory(title='Erro',
                                              message='Nenhum arquivo selecionado',
                                              type='critical',
                                              ok_button=True)
            file_not_selected.show()
            file_not_selected.wait()
            return
        
        v = Visualizer(filename)
        v.show()
        
            
    

